INSERT INTO emails VALUES(NULL, 'order_mail', '#lang_id#', 'info@ihr-shop.de', 'commerce:SEO', 'info@ihr-shop.de', 'commerce:SEO', 'Your Order - Nr: {$nr} from {$date}', '', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml" dir="ltr" xml:lang="de" lang="de">\r\n<head>\r\n<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />\r\n<title>Order Confirmation</title>\r\n{literal}\r\n<style type="text/css"> \r\nbody, table {font-size:11.5px; font-family:Helvetica, sans-serif;color:#444}\r\ntable.outerTable {border: 1px solid #ccc}\r\ntd.TopRightDesc {letter-spacing: 1px; font-weight: 600}\r\n.ProductsTable td {color:#6d88b1;background: #f1f1f1}\r\n.ProductsAttributes td, .ProductsName {background: #ffffff}\r\n.bt {border-top:1px solid #ccc}\r\n.bb {border-bottom:1px solid #ccc}\r\n.bl {border-left:1px solid #ccc}\r\n.br {border-right:1px solid #ccc}\r\n.fs85 {font-size:85%}\r\n</style>\r\n{/literal}\r\n</head>\r\n<body>\r\n<table width="90%" border="0" cellpadding="10" cellspacing="0" align="center" class="outerTable">\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellpadding="0" cellspacing="0">\r\n        <tr>\r\n          <td width="50%">\r\n            <img src="{$logo_path}logo.gif" alt="" />\r\n          </td>\r\n          <td width="50%" class="TopRightDesc" align="right">\r\n            Order Confirmation\r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n  <tr>\r\n    <td align="left">\r\n      <table width="100%" border="0" cellpadding="0" cellspacing="0">\r\n            <tr>\r\n                <td width="60%">{$address_label_customer}</td>\r\n                <td>\r\n                  <strong>Order Nr:</strong> {$oID}<br /><br />\r\n                  {if $csID}\r\n              <strong>Customers nr:</strong> {$csID}<br />\r\n            {/if}\r\n              {if $UID}\r\n              <strong>UST-ID:</strong> {$UID}<br />\r\n            {/if}\r\n                  {if $PAYMENT_METHOD}\r\n                    <strong>Payment method:</strong> {$PAYMENT_METHOD}<br />\r\n                  {/if}\r\n                  <strong>Order date:</strong> {$DATE}<br />\r\n                </td>\r\n            </tr>\r\n          </table>\r\n    </td>\r\n  </tr>\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellspacing="6" cellpadding="3" class="outerTable">\r\n            <tr bgcolor="#f1f1f1">\r\n              {if $address_label_payment == $address_label_shipping}\r\n                <td width="100%">\r\n                  <strong>Bill address</strong>\r\n                </td>\r\n          {else}\r\n            <td width="50%">\r\n                  <strong>Delivery address</strong>\r\n                </td>\r\n            <td>\r\n                <strong>Bill address</strong>\r\n            </td>\r\n          {/if}\r\n            </tr>\r\n            <tr>\r\n          {if $address_label_payment == $address_label_shipping}\r\n              <td>\r\n                {$address_label_shipping}\r\n              </td>\r\n          {else}\r\n            <td>{$address_label_shipping}</td>\r\n                  <td>{$address_label_payment}</td>\r\n                {/if}\r\n            </tr>\r\n          </table>\r\n    </td>\r\n  </tr>\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellspacing="0">\r\n        <tr>\r\n          <td><br />\r\n            Hallo {if $GENDER==''f''}Miss{else}Mister{/if} {$NNAME},<br /><br />\r\n            Thank you for your order.<br /><br />\r\n            {$PAYMENT_INFO_HTML}\r\n            <br />\r\n            {if $COMMENTS}<br />\r\n              <strong>Your comments:</strong><br />\r\n              {$COMMENTS}<br /><br />\r\n            {/if}\r\n            {if $NEW_PASSWORD}\r\n              <br /><br />\r\n              <strong>Your account password:</strong><br />\r\n              {$NEW_PASSWORD}<br /><br />\r\n            {/if}\r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellpadding="5" cellspacing="0" bgcolor="f1f1f1" class="outerTable">\r\n        <tr class="ProductsTable">\r\n          <td align="left" class="bb br">\r\n            <strong>Articel</strong>\r\n          </td>\r\n          <td align="center" class="bb br">\r\n            <strong>Single price</strong>\r\n          </td>\r\n          <td align="center" class="bb br">\r\n            <strong>Qty</strong>\r\n          </td>\r\n          <td align="right" class="bb">\r\n            <strong>Total</strong>\r\n          </td>\r\n        </tr>\r\n        {foreach name=aussen item=order_values from=$order_data}\r\n        <tr>\r\n          <td valign="top" class="bb br ProductsName">\r\n            <strong>{$order_values.PRODUCTS_NAME}</strong> <span class="fs85">(Model-Nr:{$order_values.PRODUCTS_MODEL})</span>\r\n            {if $order_values.PRODUCTS_SHIPPING_TIME neq ''''}<br />\r\n              <em>Shipping time: {$order_values.PRODUCTS_SHIPPING_TIME}</em>\r\n            {/if}\r\n          </td>\r\n          <td valign="top" class="bb br ProductsName" align="center"><strong>{$order_values.PRODUCTS_SINGLE_PRICE}</strong></td>\r\n          <td valign="top" class="bb br ProductsName" align="center"><strong>{$order_values.PRODUCTS_QTY}</strong></td>\r\n          <td valign="top" class="bb" align="right"><strong>{$order_values.PRODUCTS_PRICE}</strong></td>\r\n        </tr>\r\n        {/foreach}\r\n        {foreach name=aussen item=order_values from=$order_data}\r\n          {if $order_values.PRODUCTS_ATTRIBUTES !=''''}\r\n            <tr class="ProductsAttributes">\r\n              <td valign="top" class="bb">\r\n                {$order_values.PRODUCTS_ATTRIBUTES}\r\n              </td>\r\n              <td valign="top" align="center" class="bb">{$order_values.PRODUCTS_QTY}</td>\r\n              <td class="bb"> </td>\r\n             