INSERT INTO emails VALUES(NULL, 'recover_cart_sales', '#lang_id#', 'info@ihr-shop.de', 'commerce:SEO', 'info@ihr-shop.de', 'commerce:SEO', 'Your visit to our shop', '', '<!DOCTYPE html>\r\n<html dir="ltr" xml:lang="de" lang="de">\r\n<head>\r\n<meta http-equiv="Content-Type" content="text/html;charset=utf-8" />\r\n<title>Email</title>\r\n{literal}\r\n<style type="text/css">\r\nbody, table {font-size:11.5px; font-family:Helvetica, sans-serif;color:#444}\r\ntable.outerTable {border: 1px solid #ccc}\r\ntd.TopRightDesc {letter-spacing: 1px; font-weight: 600}\r\n.ProductsTable td {color:#6d88b1;background: #f1f1f1}\r\n.ProductsAttributes td, .ProductsName {background: #ffffff}\r\n.bt {border-top:1px solid #ccc}\r\n.bb {border-bottom:1px solid #ccc}\r\n.bl {border-left:1px solid #ccc}\r\n.br {border-right:1px solid #ccc}\r\n.fs85 {font-size:85%}\r\n</style>\r\n{/literal}\r\n</head>\r\n<body>\r\n<table width="90%" border="0" cellpadding="10" cellspacing="0" align="center" class="outerTable">\r\n  <tr>\r\n    <td>\r\n      <table width="100%" border="0" cellpadding="0" cellspacing="0">\r\n        <tr>\r\n          <td width="50%">\r\n            <img src="{$logo_path}logo.gif" alt="" />\r\n          </td>\r\n          <td width="50%" class="TopRightDesc" align="right">\r\n            Offener Warenkorb\r\n          </td>\r\n        </tr>\r\n      </table>\r\n    </td>\r\n  </tr>\r\n  <tr>\r\n    <td>{if $GENDER}Dear{if $GENDER eq ''m''} Mr.{elseif $GENDER eq ''f''} Mrs.{else} {$FIRSTNAME}{/if} {$LASTNAME},\r\n{else}Welcome,{/if}\r\n      <p>{if $NEW == true}Thank you for visiting {$STORE_NAME} and your faith and trust us.\r\n        {else}Thanks again for your visit {$STORE_NAME} and your faith and trust us repeatedly.{/if}</p>\r\n      <p>We have seen that you have filled in when you visit our online shop shopping with these articles, but the purchase has not been fully.</p>\r\n    <p>View your shopping cart:</p>\r\n    <table width="100%" border="0" cellpadding="0" cellspac